$(window).on("load",(function(){window.Parsley.addValidator("password",{requirementType:"string",validateString:function(a,e){return/^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/.test(a)},messages:{en:"Password must be at least 8 character with uppercase, lowercase, number and special symbol."}}),window.Parsley.addValidator("password2",{requirementType:"string",validateString:function(a,e){return $('input[name="password"]').val()===a},messages:{en:"Confirm password and new password must be same."}}),$("#form").parsley()}));